$(document).ready(function(){if(typeof FileActions!=='undefined'){FileActions.register('all','Compress',OC.imagePath('compress','compress.png'),function(d){if($('#dropdown').length>0){$('#dropdown').hide('blind',function(){var df=$('#dropdown').data('file');var f=$('#dir').val()+'/'+d;$('#dropdown').remove();$('tr').removeClass('mouseOver');if(df!=f){ddCompress(d,f);}});}else{ddCompress(d,$('#dir').val()+'/'+d);}});}});
function ddCompress(f,fs){var html='<div id="dropdown" class="drop" data-file="'+fs+'"><div id="ddcselect"><select data-placeholder="Algorithm" style="width:205px;" id="compress_sel" class="chzen-select"></select></div><div id="submit_ca"><input type="button" id="s_ca" value="Compress" /></div>';if(f){$('tr').filterAttr('data-file',f).addClass('mouseOver');$(html).appendTo($('tr').filterAttr('data-file',f).find('td.filename'));}$.getJSON(OC.linkTo('compress','ajax/getalgos.php'),function(a){$.each(a,function(k,v){$(v).appendTo('#compress_sel');});$('#compress_sel').trigger('liszt:updated');});$('#dropdown').show('blind');$('#compress_sel').chosen();$('#s_ca').bind('click',function(){$('#submit_ca').html('<img src="'+OC.imagePath('compress','loader.gif')+'" /><span style="margin-left:5px;">Compressing in progress</span>');$.ajax({type:'POST',url:OC.linkTo('compress','ajax/compress.php'),dataType:'json',data:{a:$('#compress_sel').val(),f:$('#dir').val()+'/'+f},async:false,success:function(k){document.location.reload();}});});}